// Global variables available: text (initial HTML), editor (Tiny HTML editor object) var dom = editor.dom; var count = 0; dom.select('h3').forEach(h3 => {
      const elementStyle = h3.style;
      if(!elementStyle || !elementStyle.getPropertyValue("font-weight")) {
           elementStyle.setPropertyValue("font-weight", 800);
           count++;
      }
}); var msg = count > 0 ? count + ' headers have been styled': 'No changes applied';  // It must return an array // [0] string (updated HTML code) or boolean (if changes are applied) // [1] string (a message for the user) return [count > 0, msg];
